mapper.update:
UPDATE TBL_WALK
SET TITLE = #{title}
, LOCATION = #{location}
, "DATE" = #{date}
, WALK_TIME = #{walk_time}
, WALK_HOUR = #{walk_hour}
, WALK_MINUTE = #{walk_minute}
, CONTENT = #{content}
, REG_DATE = SYSDATE
WHERE NUM = #{num}

UPDATE TBL_WALK_LIKE
SET LIKECHECK = #{likecheck}
WHERE BOARD_NUM = #{board_num} AND USER_ID = #{user_id}
